import React from 'react';
import Header from '../components/Header';

class Search extends React.Component {
  state = {
    inputArtistBand: '',
    disabledButton: true,
  };

  handleChangeButton = ({ target: { name, value } }) => {
    this.setState({
      [name]: value,
    });
    if (name === 'inputArtistBand') {
      const number2 = 2;
      if (value.length >= number2) {
        this.setState({
          disabledButton: false,
        });
      }
    }
  };

  render() {
    const { inputArtistBand, disabledButton } = this.state;

    return (
      <div data-testid="page-search">
        <Header />
        <form>
          <input
            type="text"
            data-testid="search-artist-input"
            name="inputArtistBand"
            value={ inputArtistBand }
            onChange={ this.handleChangeButton }
          />
          <button
            type="button"
            data-testid="search-artist-button"
            disabled={ disabledButton }
          >
            Search
          </button>
        </form>
      </div>
    );
  }
}

export default Search;
